mapper.select:
SELECT '01' AS ORDER_DVCD /*주문구분코드(01:주문, 02:신규상품주문)*/ , T1.ORDER_SEQ /*주문일련번호*/ , T1.USER_SEQ /*사용자일련번호*/ , T1.ITEM_SEQ /*상품일련번호*/ , T2.ITEM_NM /*상품명*/ , T1.PRICE /*가격*/ , T1.CNT /*개수*/ , T1.ORDER_STATUS_CODE /*주문상태코드(01:구매요청, 02:배송중, 03:미결제, 11:구매완료, 12:품절, 91:취소)*/ , (SELECT CODE_DETAIL_NM FROM TB_CODE_DETAIL WHERE CODE_GROUP = 'ORDER_STATUS_CODE' AND CODE_DETAIL = T1.ORDER_STATUS_CODE) ORDER_STATUS_CODE_NM /*주문상태코드명*/ , DATE_FORMAT(T1.ORDER_DTTI, '%Y-%m-%d %H:%i:%s') AS ORDER_DTTI /*주문일시*/ , T1.ORDER_STATUS_DTL /*주문상태상세*/ , T1.FST_REG_SEQ /*최초등록자일련번호*/ , T1.FST_REG_DTTI /*최초등록일시*/ , T1.LST_UPD_SEQ /*최종수정자일련번호*/ , T1.LST_UPD_DTTI /*최종수정일시*/ FROM TB_ORDER T1 /*테이블_주문*/ JOIN TB_ITEM T2 /*테이블_상품*/ ON T1.ITEM_SEQ = T2.ITEM_SEQ WHERE T1.USER_SEQ = #{userSeq}
AND T1.ORDER_STATUS_CODE = #{orderStatusCode}
AND T1.ORDER_STATUS_CODE IN ('01', '02', '03', '11', '12') /*주문상태코드(01:구매요청, 02:배송중, 03:미결제, 11:구매완료, 12:품절, 91:취소)*/
AND T2.ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')
AND DATE_FORMAT(T1.ORDER_DTTI, '%Y%m%d') BETWEEN DATE_FORMAT(#{startDate}, '%Y%m%d') AND DATE_FORMAT(#{endDate}, '%Y%m%d')
UNION ALL SELECT '02' AS ORDER_DVCD /*주문구분코드(01:주문, 02:신규상품주문)*/ , T1.NEW_ORDER_SEQ /*신규상품주문일련번호*/ , T1.USER_SEQ /*사용자일련번호*/ , 0 /*상품일련번호*/ , T1.ITEM_NM /*상품명*/ , T1.PRICE /*가격*/ , T1.CNT /*개수*/ , T1.ORDER_STATUS_CODE /*주문상태코드(01:구매요청, 02:배송중, 03:미결제, 11:구매완료, 12:품절, 91:취소)*/ , (SELECT CODE_DETAIL_NM FROM TB_CODE_DETAIL WHERE CODE_GROUP = 'ORDER_STATUS_CODE' AND CODE_DETAIL = T1.ORDER_STATUS_CODE) ORDER_STATUS_CODE_NM /*주문상태코드명*/ , DATE_FORMAT(T1.ORDER_DTTI, '%Y-%m-%d %H:%i:%s') AS ORDER_DTTI /*주문일시*/ , T1.ORDER_STATUS_DTL /*주문상태상세*/ , T1.FST_REG_SEQ /*최초등록자일련번호*/ , T1.FST_REG_DTTI /*최초등록일시*/ , T1.LST_UPD_SEQ /*최종수정자일련번호*/ , T1.LST_UPD_DTTI /*최종수정일시*/ FROM TB_NEW_ORDER T1 /*테이블_신규상품주문*/ WHERE T1.USER_SEQ = #{userSeq}
AND T1.ORDER_STATUS_CODE = #{orderStatusCode}
AND T1.ORDER_STATUS_CODE IN ('01', '02', '03', '11', '12') /*주문상태코드(01:구매요청, 02:배송중, 03:미결제, 11:구매완료, 12:품절, 91:취소)*/
AND T2.ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')
AND DATE_FORMAT(T1.ORDER_DTTI, '%Y%m%d') BETWEEN DATE_FORMAT(#{startDate}, '%Y%m%d') AND DATE_FORMAT(#{endDate}, '%Y%m%d')
ORDER BY ORDER_DTTI DESC

mapper.update:
UPDATE TB_ORDER
   SET ORDER_STATUS_CODE    = #{orderStatusCode}
     , LST_UPD_SEQ 			= #{userSeq}
     , LST_UPD_DTTI 		= NOW()
 WHERE ORDER_SEQ   			= #{orderSeq}
